import React, {useState} from 'react';
import { TextField, Button, Container, Stack, FormControlLabel, FormControl, FormLabel, FormHelperText, Radio, RadioGroup, Checkbox, FormGroup} from '@mui/material';
import { Link } from "react-router-dom"
 
 
const RegisterForm = () => {
    const [Title, setTitle] = React.useState('');
    const [TitleError, setTitleError] = React.useState(false);
    const [TitleHelperText, setTitleHelperText] = React.useState('Choose the title');

    const [Gender, setGender] = React.useState('');
    const [GenderError, setGenderError] = React.useState(false);
    const [GenderHelperText, setGenderHelperText] = React.useState('Choose the gender');
    
    const [firstName, setFirstName] = React.useState('');
    const [lastName, setLastName] = React.useState('');
    const [NICNo, setNICNo] = React.useState('');
    const handleNICNoChange = (e) => {
        const regex = /^[0-9V-zA-Z]+$/;
        if (e.target.value === "" || regex.test(e.target.value)) {
            setNICNo(e.target.value);
        }
    };
      
    const [Contact, setContact] = React.useState('');
    const handleContactChange = (e) => {
        const regex = /^[0-9]+$/;
        if (e.target.value === "" || regex.test(e.target.value)) {
            setContact(e.target.value);
        }
    };
    const [email, setEmail] = React.useState('');
    const [dateOfBirth, setDateOfBirth] = React.useState('');
    const [Password, setPassword] = React.useState('');
    const [ConfirmPassword, setConfirmPassword] = React.useState('');

    const [CheckBox, setChechBox] = React.useState({
        sinhala: false,
        science: false,
        buddhism: false,
        english: false,
        mathematics: false,
        chatholism: false,
        tamil: false,
        it: false,
        history: false,
    });

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
        setTitleHelperText(' ');
        setTitleError(false);
    };

    const handleGenderChange = (event) => {
        setGender(event.target.value);
        setGenderHelperText(' ');
        setGenderError(false);
    };

    const handleCheckBoxChange = (event) => {
        setChechBox({
          ...CheckBox,
          [event.target.name]: event.target.checked,
        });
    };

    const { sinhala, science, buddhism, english, mathematics, chatholism, tamil, it, history } = CheckBox;
    const CheckBoxError = [sinhala, science, buddhism, english, mathematics, chatholism, tamil, it, history].filter((v) => v).length !== 2;
    
 
    function handleSubmit(event) {
        event.preventDefault();

        if (Title === 'mr') {
            setTitleError(false);
            console.log(Title);
          } else if (Title === 'mrs') {
            setTitleError(false);
            console.log(Title);
          } else if (Title === 'ms') {
              setTitleError(false);
              console.log(Title);
          }else {
            setTitleHelperText('Please select an option.');
            setTitleError(true);
            console.log('error');
          }
          
          if (Gender === 'male') {
            setGenderError(false);
            console.log(Gender);
          } else if (Gender === 'female') {
            setGenderError(false);
            console.log(Gender);
          } else {
            setGenderHelperText('Please select an option.');
            setGenderError(true);
            console.log('error');
          }
          
          if (CheckBoxError === false){
            console.log(CheckBox);
          }
          else {
             console.log('error');
         }

        console.log(firstName, lastName, NICNo, email, dateOfBirth, password) 
    }
 
    return (
        <Container maxWidth="md">
        <React.Fragment>
            <h2>Register Form</h2>
            <form onSubmit={handleSubmit} action={<Link to="/login" />}>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                <FormControl fullWidth error={TitleError} variant="standard">
                    <FormLabel id="demo-radio-buttons-group-label">Title</FormLabel>
                        <RadioGroup
                        aria-labelledby="demo-error-radios"
                        name="title"
                        value={Title}
                        onChange={handleTitleChange}
                        row
                        >
                        <FormControlLabel value="mr" control={<Radio />} label="Mr." />
                        <FormControlLabel value="mrs" control={<Radio />} label="Mrs." />
                        <FormControlLabel value="ms" control={<Radio />} label="Ms." />
                        </RadioGroup>
                        <FormHelperText>{TitleHelperText}</FormHelperText>
                </FormControl>
                <FormControl fullWidth error={GenderError} variant="standard">
                    <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                        <RadioGroup
                        aria-labelledby="demo-error-radios"
                        name="gender"
                        value={Gender}
                        onChange={handleGenderChange}
                        row
                        >
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        </RadioGroup>
                        <FormHelperText>{GenderHelperText}</FormHelperText>
                </FormControl>
                </Stack>
                <Stack spacing={2} direction="row" sx={{marginBottom: 2}}>
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="First Name"
                        onChange={e => setFirstName(e.target.value)}
                        value={firstName}
                        fullWidth
                        required
                    />
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="Last Name"
                        onChange={e => setLastName(e.target.value)}
                        value={lastName}
                        fullWidth
                        required
                    />
                </Stack>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                    <TextField
                        type="date"
                        variant='outlined'
                        color='primary'
                        label="Date of Birth"
                        InputLabelProps={{ shrink: true, required: true }}
                        onChange={e => setDateOfBirth(e.target.value)}
                        value={dateOfBirth}
                        fullWidth
                    />
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="NIC Number"
                        onChange={(e) => handleNICNoChange(e)}
                        value={NICNo}
                        fullWidth
                        required
                        inputProps={{ maxLength: 10 }}
                    />
                </Stack>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                <TextField
                    type="email"
                    variant='outlined'
                    color='primary'
                    label="Email"
                    onChange={e => setEmail(e.target.value)}
                    value={email}
                    fullWidth
                    required
                />
                <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="Contact Number"
                        onChange={(e) => handleContactChange(e)}
                        value={Contact}
                        fullWidth
                        required
                        inputProps={{ maxLength: 10 }}
                    />
                </Stack>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                <TextField
                    type="password"
                    variant='outlined'
                    color='primary'
                    label="Password"
                    onChange={e => setPassword(e.target.value)}
                    value={Password}
                    required
                    fullWidth
                />
                <TextField
                    type="password"
                    variant='outlined'
                    color='primary'
                    label="Confirm Password"
                    onChange={e => setConfirmPassword(e.target.value)}
                    value={ConfirmPassword}
                    required
                    fullWidth
                />
                </Stack>
                <Stack spacing={2} direction="row" sx={{marginBottom: 2}} fullWidth>
                    <FormControl
                        required
                        error={CheckBoxError}
                        component="fieldset"
                        variant="standard"
                        fullWidth
                    >
                        <FormLabel component="legend">Skills</FormLabel>
                        <FormGroup 
                            row
                        >
                        <FormControlLabel   
                            control={
                            <Checkbox checked={sinhala} onChange={handleCheckBoxChange} name="sinhala" />
                            }
                            label="Sinhala"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={science} onChange={handleCheckBoxChange} name="science" sx={{ml: 30}}/>
                            }
                            label="Science"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={buddhism} onChange={handleCheckBoxChange} name="buddhism" sx={{ml: 34}}/>
                            }
                            label="Buddhism"
                        />
                        </FormGroup>
                        <FormGroup 
                            row
                        >
                        <FormControlLabel
                            control={
                            <Checkbox checked={english} onChange={handleCheckBoxChange} name="english" />
                            }
                            label="English"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={mathematics} onChange={handleCheckBoxChange} name="mathematics" sx={{ml: 30}}/>
                            }
                            label="Mathematics"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={chatholism} onChange={handleCheckBoxChange} name="chatholism" sx={{ml: 30}}/>
                            }
                            label="Chatholism"
                        />
                        </FormGroup>
                        <FormGroup 
                            row
                        >
                        <FormControlLabel
                            control={
                            <Checkbox checked={tamil} onChange={handleCheckBoxChange} name="tamil" />
                            }
                            label="Tamil" 
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={it} onChange={handleCheckBoxChange} name="it" sx={{ml: 32}}/>
                            }
                            label="IT"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={history} onChange={handleCheckBoxChange} name="history" sx={{ml: 39.6}}/>
                            }
                            label="History"
                        />
                        </FormGroup>
                        <FormHelperText>You can display an error</FormHelperText>
                    </FormControl>
                    
                </Stack>
                
                <Button variant="outlined" color="primary" type="submit">Register</Button>
            </form>
            <small>Already have an account? <Link to="/login">Login Here</Link></small>
     
        </React.Fragment>
        </Container>
    )
}
 
export default RegisterForm;
