import React, { useState, useEffect } from 'react';
import { Container, Typography } from '@mui/material';
import { Box } from '@material-ui/core';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import MailIcon from '@mui/icons-material/Mail';
import CallIcon from '@mui/icons-material/Call';
import { makeStyles } from '@mui/styles';

import Divider from '@mui/material/Divider';

const useStyles = makeStyles({
    container: {
      backgroundColor: 'rgba(0, 0, 0, 0.125)',
    },
  });

const UserDetails = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/mentor/profile/646aaddf1d7d80bab67f0d70', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => response.json())
      .then(data => setUser(data))
      .catch(error => console.log(error));
  }, []);


  const classes = useStyles();
  
  return (
    <>
    <br/>
    <br/>
    <Typography variant='h2'>Mentor Profile</Typography>
    <br/>
    <br/>
    <br/>
    <br/>

    <Container className={classes.container}>

    

      {user ? (
        <div>

        
          <Typography variant="h4">{user.title} {user.firstName} {user.lastName}</Typography>

          <Typography variant="body1">Description: {user.description}</Typography>
          
          <Box>
            <Typography variant="body1">NIC No: {user.nicNo}</Typography>
            <Typography variant="body1">Date of Birth: {user.dateOfBirth}</Typography>

          </Box>
          <List
            sx={{

                width: '100%',
                maxWidth: 360,
                bgcolor: 'background.paper',
            }}
            >
              <ListItem>
                <ListItemAvatar>
                  <Avatar>
                    <MailIcon />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText  primary="Email" secondary={user.email} />
              </ListItem>
              
              <Divider variant="inset" component="li" />

              <ListItem>
                <ListItemAvatar>
                  <Avatar>
                    <CallIcon />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText primary="Contact number" secondary={user.contact} />
              </ListItem>

              <Divider variant="inset" component="li" />

    
            </List>

            
          
          
          <Typography variant="body1">Skills: {user.skills.join(', ')}</Typography>
        </div>
      ) : (
        <Typography variant="body1">Loading user data...</Typography>
      )}
    </Container>
    </>
  );
};

export default UserDetails;
