import { Button } from '@mui/material'
import React, { useState } from 'react'
import { useLocation } from 'react-router-dom';
import '../App.css'



export default function DeleteProfile() {
  const location = useLocation();
  const [isDeleted, setIsDeleted] = useState(false);
  
  const handleDelete = () => {
    const userId = (location.state.id)
    const confirmDelete = window.confirm('Are you sure you want to delete this item?');

    if (confirmDelete) {
      
      const apiUrl = 'http://localhost:5000/mentor/delete/${userId}';

     
      fetch(apiUrl, {
        method: 'DELETE'
      })
        .then(response => {
          if (response.ok) {
            
            setIsDeleted(true);
          } else {
            throw new Error('Delete request failed');
          }
        })
        .catch(error => {
          // Handle the error
          console.log(error);
        });
    }
  };
  return (
    <div >
      <br/>
      <br/>
      <br/>
      <br/>
      <h2 className='warn'>Your profile will be deleted..!</h2>
      <br/>
      <Button variant="contained" size='large' color="error" onClick={handleDelete}> Confirm</Button>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      const apiUrl = 'http://localhost:5000/mentor/delete/646aaddf1d7d80bab67f0d70';
    </div>
  )
}
