import { Button } from '@mui/material'
import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../App.css'



export default function DeleteProfile() {

    const [isDeleted, setIsDeleted] = useState(false);
    const history = useHistory();
  
    const handleDelete = () => {
      const confirmDelete = window.confirm('Are you sure you want to delete this item?');
      if (!confirmDelete) return;
  
      const apiUrl = 'http://localhost:5000/mentor/delete/646aaddf1d7d80bab67f0d70';
  
      fetch(apiUrl, { method: 'DELETE' })
        .then(response => {
          if (response.ok) {
            setIsDeleted(true);
            toast.success('Item deleted');
            history.push('/home');
          } else {
            throw new Error('Error deleting item');
          }
        })
        .catch(error => {
          console.log(error);
          toast.error('Error deleting item');
        });
    };
  return (
    <div >
      <br/>
      <br/>
      <br/>
      <br/>
      <h2 className='warn'>Your profile will be deleted..!</h2>
      <br/>
      <Button variant="contained" size='large' color="error" onClick={handleDelete}> Confirm</Button>
      <br/>
      <br/>
      <br/>
      <br/>
      <br/>
      const apiUrl = 'http://localhost:5000/mentor/delete/646aaddf1d7d80bab67f0d70';
    </div>
  )
}
