import React from 'react';
import { TextField, Button, Container, Stack, FormControlLabel, FormControl, FormLabel, FormHelperText, Radio, RadioGroup, Checkbox, FormGroup} from '@mui/material';
import { Link } from "react-router-dom"
import './styles.css';

 
 
const RegisterForm = () => {
    const [Title, setTitle] = React.useState('');
    const [TitleError, setTitleError] = React.useState(false);
    const [TitleHelperText, setTitleHelperText] = React.useState('Choose the title');
    const handleTitleChange = (event) => {
        setTitle(event.target.value);
        setTitleHelperText(' ');
        setTitleError(false);
    };

    const [Gender, setGender] = React.useState('');
    const [GenderError, setGenderError] = React.useState(false);
    const [GenderHelperText, setGenderHelperText] = React.useState('Choose the gender');
    
    const handleGenderChange = (event) => {
        setGender(event.target.value);
        setGenderHelperText(' ');
        setGenderError(false);
    };
    
    const [FirstName, setFirstName] = React.useState('');

    const [LastName, setLastName] = React.useState('');

    const [NICNo, setNICNo] = React.useState('');
    const [NICNoError, setNICNoError] = React.useState(false);
    const [NICNoHelperText, setNICNoHelperText] = React.useState('');
    const handleNICNoChange = (e) => {
        const regex = /^[0-9-Vv]+$/;
        if (e.target.value === "" || regex.test(e.target.value)) {
            setNICNo(e.target.value);
        }
        setNICNoError(false);
        setNICNoHelperText('');
    };
      
    const [Contact, setContact] = React.useState('');
    const [ContactError, setContactError] = React.useState(false);
    const [ContactHelperText, setContactHelperText] = React.useState('');
    const handleContactChange = (e) => {
        const regex = /^[0-9]+$/;
        if (e.target.value === "" || regex.test(e.target.value)) {
            setContact(e.target.value);
        }
        setContactError(false);
        setContactHelperText('');
    };

    const [Email, setEmail] = React.useState('');

    const [DateOfBirth, setDateOfBirth] = React.useState('');

    const [InputPassword, setInputPassword] = React.useState({
        password: '',
        confirmPassword: '',
      });
    
      const [PasswordError, setPasswordError] = React.useState({
        password: '',
        confirmPassword: '',
      });

      const onInputPasswordChange = (e) => {
        const { name, value } = e.target;
        setInputPassword((prev) => ({
          ...prev,
          [name]: value,
        }));
        validateInputPassword(e);
      };
    
      const validateInputPassword = (e) => {
        let { name, value } = e.target;
        setPasswordError((prev) => {
          const stateObj = { ...prev, [name]: '' };
    
          switch (name) {
            case 'password':
              if (!value) {
                stateObj[name] = 'Please enter Password.';
              } else if (InputPassword.confirmPassword && value !== InputPassword.confirmPassword) {
                stateObj['confirmPassword'] =
                  'Password and Confirm Password does not match.';
              } else {
                stateObj['confirmPassword'] = InputPassword.confirmPassword
                  ? ''
                  : PasswordError.confirmPassword;
              }
              break;
    
            case 'confirmPassword':
              if (!value) {
                stateObj[name] = 'Please enter Confirm Password.';
              } else if (InputPassword.password && value !== InputPassword.password) {
                stateObj[name] = 'Password and Confirm Password does not match.';
              }
              break;
    
            default:
              break;
          }
    
          return stateObj;
        });
      };

    const [CheckBox, setChechBox] = React.useState({
      sinhala: false,
      science: false,
      buddhism: false,
      english: false,
      mathematics: false,
      chatholism: false,
      tamil: false,
      it: false,
      history: false,
    });
    const handleCheckBoxChange = (event) => {
        setChechBox({
          ...CheckBox,
          [event.target.name]: event.target.checked,
        });
    };
    const { sinhala, science, buddhism, english, mathematics, chatholism, tamil, it, history } = CheckBox;
    const CheckBoxError = [sinhala, science, buddhism, english, mathematics, chatholism, tamil, it, history].filter((v) => v).length > 3;
    
    const [Discription, setDiscription] = React.useState('');
    const [DiscriptionError, setDiscriptionError] = React.useState(false);
    const [DiscriptionHelperText, setDiscriptionHelperText] = React.useState('');
    const handleDiscriptionChange = (event) => {
      setDiscription(event.target.value);
      setDiscriptionHelperText(' ');
      setDiscriptionError(false);
  };


 
  
    
    function handleSubmit(event) {

      let check = 0;
      let sendTitle;
      let sendGender;
      let sendNICNo;
      let sendContact;
      let sendInputPassword;
      let sendCheckBox;
      let sendDescription;

        if (Title === 'mr') {
            setTitleError(false);
            check += 1;
            sendTitle = Title
            console.log(check, sendTitle);
        } else if (Title === 'mrs') {
            setTitleError(false);
            sendTitle = Title
            console.log(sendTitle);
        } else if (Title === 'ms') {
            setTitleError(false);
            sendTitle = Title
            console.log(sendTitle);
        }else {
           setTitleHelperText('Please select an option.');
           setTitleError(true);
           event.preventDefault();
        }
          
        if (Gender === 'male') {
          setGenderError(false);
          check += 1;
          sendGender = Gender
          console.log(check, sendGender);
        } else if (Gender === 'female') {
          setGenderError(false);
          sendGender = Gender
          console.log(sendGender);
        } else {
          setGenderHelperText('Please select an option.');
          setGenderError(true);
          event.preventDefault();
        }
        

        console.log(FirstName);

        console.log(LastName);

        console.log(DateOfBirth);

        if (NICNo.length === 10) {
          sendNICNo = NICNo;
          console.log(sendNICNo);
        }
        else {
          setNICNoHelperText('Check your NIC No');
          setNICNoError(true);
          event.preventDefault();
        }

        console.log(Email);

        if (Contact.length === 10) {
          sendContact = Contact
          console.log(sendContact);
        }
        else {
          setContactHelperText('Check your Contact No');
          setContactError(true);
          event.preventDefault();
        }

        console.log(InputPassword.confirmPassword);
        
        if (CheckBoxError === false){
          sendCheckBox = CheckBox;
          console.log(sendCheckBox);
        }
        else {
          event.preventDefault();
        }

        if (Discription.length > 20) {
          sendDescription = Discription
          console.log(sendDescription);
        }
        else {
          setDiscriptionHelperText('Type a long Description!');
          setDiscriptionError(true);
          event.preventDefault();
        }

        const mentorData = {
          title: sendTitle,
          gender: sendGender,
          firstName: FirstName,
          lastName: LastName,
          nicNo: sendNICNo,
          contact: sendContact,
          email: Email,
          dateOfBirth: DateOfBirth,
          password: sendInputPassword.confirmPassword,
          skills: sendCheckBox,
          description: sendDescription,
        };
    
        // Send the mentorData object to the server
        fetch('http://localhost:5000/mentor/add', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(mentorData),
        })
          .then(response => response.json())
          .then(data => {
            // Handle the response from the server
            console.log('Response:', data);
          })
          .catch(error => {
            console.error('Error:', error);
          });
        
    }
 
    return (
        <Container maxWidth="md">
        <React.Fragment>
            <h2>Register Form</h2>
            <form onSubmit={handleSubmit}>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                <FormControl fullWidth error={TitleError} variant="standard">
                    <FormLabel id="demo-radio-buttons-group-label">Title</FormLabel>
                        <RadioGroup
                        aria-labelledby="demo-error-radios"
                        name="title"
                        value={Title}
                        onChange={handleTitleChange}
                        row
                        >
                        <FormControlLabel value="mr" control={<Radio />} label="Mr." />
                        <FormControlLabel value="mrs" control={<Radio />} label="Mrs." />
                        <FormControlLabel value="ms" control={<Radio />} label="Ms." />
                        </RadioGroup>
                        <FormHelperText>{TitleHelperText}</FormHelperText>
                </FormControl>
                <FormControl fullWidth error={GenderError} variant="standard">
                    <FormLabel id="demo-radio-buttons-group-label">Gender</FormLabel>
                        <RadioGroup
                        aria-labelledby="demo-error-radios"
                        name="gender"
                        value={Gender}
                        onChange={handleGenderChange}
                        row
                        >
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        </RadioGroup>
                        <FormHelperText>{GenderHelperText}</FormHelperText>
                </FormControl>
                </Stack>
                <Stack spacing={2} direction="row" sx={{marginBottom: 2}}>
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="First Name"
                        onChange={e => setFirstName(e.target.value)}
                        value={FirstName}
                        fullWidth
                        required
                    />
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="Last Name"
                        onChange={e => setLastName(e.target.value)}
                        value={LastName}
                        fullWidth
                        required
                    />
                </Stack>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                    <TextField
                        type="date"
                        variant='outlined'
                        color='primary'
                        label="Date of Birth"
                        InputLabelProps={{ shrink: true, required: true }}
                        onChange={e => setDateOfBirth(e.target.value)}
                        value={DateOfBirth}
                        fullWidth
                        required
                    />
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="NIC Number"
                        onChange={handleNICNoChange}
                        value={NICNo}
                        fullWidth
                        required
                        inputProps={{ maxLength: 10 }}
                        helperText={NICNoHelperText}
                        error={NICNoError}
                    />
                </Stack>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                    <TextField
                        type="email"
                        variant='outlined'
                        color='primary'
                        label="Email"
                        onChange={e => setEmail(e.target.value)}
                        value={Email}
                        fullWidth
                        required
                    />
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="Contact Number"
                        onChange={handleContactChange}
                        value={Contact}
                        fullWidth
                        required
                        inputProps={{ maxLength: 10 }}
                        helperText={ContactHelperText}
                        error={ContactError}
                    />
                </Stack>
                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                <input
                    className='InputPass'
                    type="password"
                    name="password"
                    variant='outlined'
                    color='primary'
                    placeholder="Enter Password *"
                    value={InputPassword.password}
                    onChange={onInputPasswordChange}
                    onBlur={validateInputPassword}
                    required
                ></input>
                    {PasswordError.password && <span className="err">{PasswordError.password}</span>}

                <input
                    className='InputPass'
                    type="password"
                    name="confirmPassword"
                    variant='outlined'
                    color='primary'
                    placeholder="Enter Confirm Password *"
                    value={InputPassword.confirmPassword}
                    onChange={onInputPasswordChange}
                    onBlur={validateInputPassword}
                    fullWidth
                    required
                ></input>
                    {PasswordError.confirmPassword && (
                    <span className="err">{PasswordError.confirmPassword}</span>
                    )}
                </Stack>
                <Stack spacing={2} direction="row" sx={{marginBottom: 2}} fullWidth>
                    <FormControl
                        required
                        error={CheckBoxError}
                        component="fieldset"
                        variant="standard"
                        fullWidth
                    >
                        <FormLabel component="legend">Skills</FormLabel>
                        <FormGroup 
                            row
                        >
                        <FormControlLabel   
                            control={
                            <Checkbox checked={sinhala} onChange={handleCheckBoxChange} name="sinhala" />
                            }
                            label="Sinhala"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={science} onChange={handleCheckBoxChange} name="science" sx={{ml: 30}}/>
                            }
                            label="Science"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={buddhism} onChange={handleCheckBoxChange} name="buddhism" sx={{ml: 34}}/>
                            }
                            label="Buddhism"
                        />
                        </FormGroup>
                        <FormGroup 
                            row
                        >
                        <FormControlLabel
                            control={
                            <Checkbox checked={english} onChange={handleCheckBoxChange} name="english" />
                            }
                            label="English"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={mathematics} onChange={handleCheckBoxChange} name="mathematics" sx={{ml: 30}}/>
                            }
                            label="Mathematics"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={chatholism} onChange={handleCheckBoxChange} name="chatholism" sx={{ml: 30}}/>
                            }
                            label="Chatholism"
                        />
                        </FormGroup>
                        <FormGroup 
                            row
                        >
                        <FormControlLabel
                            control={
                            <Checkbox checked={tamil} onChange={handleCheckBoxChange} name="tamil" />
                            }
                            label="Tamil" 
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={it} onChange={handleCheckBoxChange} name="it" sx={{ml: 32}}/>
                            }
                            label="IT"
                        />
                        <FormControlLabel
                            control={
                            <Checkbox checked={history} onChange={handleCheckBoxChange} name="history" sx={{ml: 39.6}}/>
                            }
                            label="History"
                        />
                        </FormGroup>
                        <FormHelperText>Please select no more than 3 skill sets</FormHelperText>
                    </FormControl>
                    
                </Stack>

                <Stack spacing={2} textAlign={'left'} direction="row" sx={{marginBottom: 2}} >
                    <TextField
                        type="text"
                        variant='outlined'
                        color='primary'
                        label="Discription"
                        onChange={handleDiscriptionChange}
                        value={Discription}
                        fullWidth
                        multiline
                        rows={4}
                        required
                        helperText={DiscriptionHelperText}
                        error={DiscriptionError}
                    />
                    
                    </Stack>
                
                <Button variant="outlined" color="primary" type="submit">Register</Button>
            </form>
            <br/>
           
            <small>Already have an account? <Link to="/login">Login Here</Link></small>


            <br/>
            <br/>
            <br/>
            <br/>
     
        </React.Fragment>
        </Container>
    )
}
 
export default RegisterForm;
